CREATE DATABASE IF NOT EXISTS LVBsp;USE LVBsp;DROP TABLE IF EXISTS Studiengang;CREATE TABLE Studiengang (  SGID VARCHAR(16) NOT NULL,  Bezeichnung VARCHAR(128),  Abschluss VARCHAR(16),  PRIMARY KEY (SGID));DROP TABLE IF EXISTS Studenten;CREATE TABLE Studenten (  MatrNr VARCHAR(16) NOT NULL,  Name VARCHAR(64),  Strasse VARCHAR(64),  PLZ VARCHAR(5),  ORT VARCHAR(16),  Telefon VARCHAR(16),  EmailAdresse VARCHAR(64),  SGID VARCHAR(16),  PRIMARY KEY (MatrNr),  FOREIGN KEY (SGID) REFERENCES Studiengang (SGID));DROP TABLE IF EXISTS Lehrveranstaltung;CREATE TABLE Lehrveranstaltung (  LVID VARCHAR(16) NOT NULL,  Raum VARCHAR(16),  Semester INT,  Titel VARCHAR(256),  Zeit VARCHAR(16),  PRIMARY KEY (LVID));DROP TABLE IF EXISTS besteht_aus;CREATE TABLE besteht_aus (  SGID VARCHAR(16) NOT NULL,  LVID VARCHAR(16) NOT NULL,  PRIMARY KEY (SGID, LVID),  FOREIGN KEY (SGID) REFERENCES Studiengang (SGID),  FOREIGN KEY (LVID) REFERENCES Lehrveranstaltung (LVID));DROP TABLE IF EXISTS besuchen;CREATE TABLE besuchen (  MatrNr VARCHAR(16) NOT NULL,  LVID VARCHAR(16) NOT NULL,  Note INT,  PRIMARY KEY (MatrNr, LVID),  FOREIGN KEY (MatrNr) REFERENCES Studenten (MatrNr),  FOREIGN KEY (LVID) REFERENCES Lehrveranstaltung (LVID));

